import React,{useState} from 'react'
import './segunda.css'

function Segunda() {
    const [lastComentario,setLastComentario]=useState("teste")
    const send=(e) => {
        console.log(e)
        if (e.charCode===13){
            setLastComentario("comenatrio")
        }
    }
    
    return(
        <div className="painel">
            <input onKeyPress={(e)=>send(e)} type="text" className="segunda" defaultValue={lastComentario}/>
            <p>{}</p>
        </div>
        
    );
}
export default Segunda;
